import { CardContent, Grid, Typography, Box, Card, makeStyles } from "@material-ui/core"
import CountUp from 'react-countup';


const useStyle = makeStyles({
    header:{
        background: '#F5F5F5',
        padding:10
    }
})

const CardComponent = ({cardTitle, value, desc, lastUpdate}) => {

    return (
        <Grid component={Card} style={{margin:20,borderBottom: '10px solid #8ACA2B'}}>
            <Box>
                <Typography variant="h5" color="textSecondary">{cardTitle}</Typography>
            </Box>
            <CardContent>
                <Typography variant="h5">
                <CountUp start={0} end={value} duration={3} seperator=","/>
                </Typography>
                <Typography>{desc}</Typography>
                <Typography>{new Date(lastUpdate).toDateString()}</Typography>
            </CardContent>
        </Grid>
    );

}


export default CardComponent;
